function handleSubscription(){const e=document.getElementById("emailInput").value;event.preventDefault(),validateEmail(e)?(displayMessage("Thank you for subscribing! Welcome to the Move It family!","success"),document.getElementById("subscribeForm").reset()):displayMessage("Please enter a valid email address.","danger")}function validateEmail(e){return/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(String(e).toLowerCase())}function displayMessage(e,s){const t=document.getElementById("responseMessage");t.textContent=e,t.className="success"===s?"text-success":"text-danger"}
