function generateIdentifier(){return Math.random().toString(36).substr(2,9)}function getReferralIdentifier(){let e=localStorage.getItem("referralId");return e||(e=generateIdentifier(),localStorage.setItem("referralId",e)),e}const link=document.getElementById("referralLink");function updateReferralLink(){const e=`https://primesolar.github.io/move-it/?referralId=${getReferralIdentifier()}`;link.value=e}function copyReferralLink(){link.select(),document.execCommand("copy");const e=event.target;e.textContent="Copied!",setTimeout((()=>{e.textContent="Copy"}),1e3)}window.onload=updateReferralLink;
