function generateIdentifier(){return Math.random().toString(36).substr(2,9)}function getReferralIdentifier(){let e=localStorage.getItem("referralId");return e||(e=generateIdentifier(),localStorage.setItem("referralId",e)),e}function updateReferralLink(){const e=`https://primesolar.github.io/move-it/?referralId=${getReferralIdentifier()}`;document.getElementById("referralLink").value=e}function copyReferralLink(){document.getElementById("referralLink").select(),document.execCommand("copy");const e=event.target;e.textContent="Copied!",setTimeout((()=>{e.textContent="Copy"}),1e3)}window.onload=updateReferralLink;
