document.addEventListener("DOMContentLoaded",(()=>{const e=document.createElement("style");e.textContent="\n/* Slide Animation */\n\n.slide-animation {\n  opacity: 0;\n  transform: translateY(30px);\n  transition: opacity 0.57s ease-out, transform 0.57s ease-out;\n}\n\n.slide-animation.visible {\n  opacity: 1;\n  transform: translateY(0);\n}",document.head.appendChild(e);const t=document.querySelectorAll("img:not(.d-block.w-100.rounded-5), p");for(let e of t)e.classList.add("slide-animation");const n=new IntersectionObserver((e=>{for(let t of e)t.isIntersecting&&(t.target.classList.add("visible"),n.unobserve(t.target))}),{threshold:.25});for(let e of t)n.observe(e);const o={log:(...e)=>{}};setInterval((()=>{const e=[];document.querySelectorAll(".slide-animation").forEach((t=>{t.classList.contains("visible")&&e.push(t)})),o.log(`Currently ${e.length} out of ${document.querySelectorAll(".slide-animation").length} animated elements are visible.`)}),5e3)}));
