document.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelectorAll("img:not(.d-block.w-100.rounded-5), p");for(let t of e)t.classList.add("slide-animation");const t=new IntersectionObserver((e=>{for(let o of e)o.isIntersecting&&(o.target.classList.add("visible"),t.unobserve(o.target))}),{threshold:.25});for(let o of e)t.observe(o)}));const Logger={log:(...e)=>{}},logActiveAnimations=()=>{const e=[];document.querySelectorAll(".slide-animation").forEach((t=>{t.classList.contains("visible")&&e.push(t)})),Logger.log(`Currently ${e.length} out of ${document.querySelectorAll(".slide-animation").length} animated elements are visible.`)};setInterval(logActiveAnimations,5e3);
